void printRoundConstants(){
    const int s[27] = {1,2,4,8,16,32,64,128,256,512,1024,2048,4096,8192, 16384,32768,65536,131072,262144,524288,1048576,2097152,4194304,8388608,16777216,33554432,67108864};
    for (int round = 0; round < NUM_ROUNDS; ++round){
        for (int col = 0; col < COLUMNS; ++col) {
            int p = 0, n = 0;
            for (int slice = 0; slice < SLICES; ++slice){
                if (round_constants[round][slice*COLUMNS+col]==1){
                    p += s[slice];
                }
                if (round_constants[round][slice*COLUMNS+col]==2){
                    n += s[slice];
                }
            }
            printf("{%d,%d}",p,n);
            if (col < COLUMNS-1) printf(", ");
        }
        printf("},\n{");
    }
}

